import { Works, WorkCategory, Technology } from "../data/works";

/**
 * ÁîªÂÉè„ÅÆÂ≠òÂú®„ÇíÁ¢∫Ë™ç„Åô„ÇãÈñ¢Êï∞
 */
const checkImageExists = (src: string): Promise<boolean> => {
  return new Promise((resolve) => {
    const img = new Image();
    img.onload = () => resolve(true);
    img.onerror = () => resolve(false);
    img.src = src;
  });
};

/**
 * public/images/worksÂÜÖ„ÅÆÈÄ£Áï™„Éï„Ç©„É´„ÉÄ„ÇíÊ§úÂá∫„Åô„ÇãÈñ¢Êï∞
 * „Çµ„É†„Éç„Ç§„É´ÁîªÂÉè„ÅÆÂ≠òÂú®„ÇíÁ¢∫Ë™ç„Åó„Å¶„ÄÅÊúâÂäπ„Å™„Éï„Ç©„É´„ÉÄ„ÇíÂà§ÂÆö
 */
export async function detectWorksFolders(
  maxId: number = 20
): Promise<number[]> {
  const detectedFolders: number[] = [];
  const checkPromises: Promise<boolean>[] = [];

  // 1„Åã„ÇâmaxId„Åæ„ÅßÈ†ÜÁï™„Å´„ÉÅ„Çß„ÉÉ„ÇØ
  for (let id = 1; id <= maxId; id++) {
    const thumbnailPath = `/images/works/${id}/thumbnail.webp`;
    checkPromises.push(
      checkImageExists(thumbnailPath).then((exists) => {
        if (exists) {
          detectedFolders.push(id);
        }
        return exists;
      })
    );
  }

  // ‰∏¶Âàó„ÅßÁîªÂÉè„ÅÆÂ≠òÂú®„ÇíÁ¢∫Ë™ç
  await Promise.all(checkPromises);

  // Êï∞ÂÄ§È†Ü„Å´„ÇΩ„Éº„Éà
  return detectedFolders.sort((a, b) => a - b);
}

/**
 * „Éá„Éï„Ç©„É´„Éà„ÅÆworks„Éá„Éº„Çø„ÇíÁîüÊàê„Åô„ÇãÈñ¢Êï∞
 */
export function generateDefaultWorkData(id: number): Works {
  const currentDate = new Date();
  const year = currentDate.getFullYear();
  const month = String(currentDate.getMonth() + 1).padStart(2, "0");
  const dateString = `${year}.${month}`;

  return {
    id,
    title: `‰ΩúÂìÅ ${id}`,
    date: dateString,
    role: "ÈñãÁô∫„Éª„Éá„Ç∂„Ç§„É≥",
    client: "„ÇØ„É©„Ç§„Ç¢„É≥„ÉàÂêç",
    industry: "„Åù„ÅÆ‰ªñ",
    technologies: ["HTML", "CSS", "JavaScript"] as Technology[],
    category: ["„Çµ„Éº„Éì„Çπ„Çµ„Ç§„Éà"] as WorkCategory[],
    details: {
      overview: ["‰ΩúÂìÅ„ÅÆÊ¶ÇË¶Å„Çí„Åì„Åì„Å´Ë®òÂÖ•„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ"],
      challenge: ["Ë™≤È°å„Çí„Åì„Åì„Å´Ë®òÂÖ•„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ"],
      solution: ["Ëß£Ê±∫Á≠ñ„Çí„Åì„Åì„Å´Ë®òÂÖ•„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ"],
      result: ["ÊàêÊûú„Çí„Åì„Åì„Å´Ë®òÂÖ•„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ"],
      features: ["‰∏ª„Å™Ê©üËÉΩ„Çí„Åì„Åì„Å´Ë®òÂÖ•„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ"],
      link: "",
    },
    mediaData: {
      images: [],
      videos: [],
    },
    isVisible: true,
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString(),
  };
}

/**
 * Êó¢Â≠ò„ÅÆworks„Éá„Éº„Çø„Å®Ëá™ÂãïÁîüÊàê„Éá„Éº„Çø„Çí„Éû„Éº„Ç∏„Åô„ÇãÈñ¢Êï∞
 * Êó¢Â≠ò„Éá„Éº„Çø„ÇíÂÑ™ÂÖà„Åó„ÄÅÂ≠òÂú®„Åó„Å™„ÅÑID„ÅÆ„ÅøËá™ÂãïÁîüÊàê„Éá„Éº„Çø„ÇíËøΩÂä†
 */
export function mergeWorksData(
  existingWorks: Works[],
  autoGeneratedWorks: Works[]
): Works[] {
  const existingIds = new Set(existingWorks.map((work) => work.id));
  const newWorks = autoGeneratedWorks.filter(
    (work) => !existingIds.has(work.id)
  );

  // Êó¢Â≠ò„Éá„Éº„Çø„Å®Êñ∞Ë¶è„Éá„Éº„Çø„Çí„Éû„Éº„Ç∏ÔºàÊó¢Â≠ò„Éá„Éº„Çø„ÇíÂÑ™ÂÖàÔºâ
  return [...existingWorks, ...newWorks].sort((a, b) => a.id - b.id);
}

/**
 * „Éï„Ç©„É´„ÉÄ„Åã„ÇâËá™ÂãïÁöÑ„Å´works„Éá„Éº„Çø„ÇíÁîüÊàê„Åô„ÇãÈñ¢Êï∞
 */
export async function autoGenerateWorksData(
  existingWorks: Works[]
): Promise<Works[]> {
  console.log("üîç works„Éï„Ç©„É´„ÉÄ„ÇíËá™ÂãïÊ§úÂá∫‰∏≠...");

  // „Éï„Ç©„É´„ÉÄ„ÇíÊ§úÂá∫
  const detectedFolders = await detectWorksFolders(20);
  console.log(
    `‚úÖ ${detectedFolders.length}ÂÄã„ÅÆworks„Éï„Ç©„É´„ÉÄ„ÇíÊ§úÂá∫„Åó„Åæ„Åó„Åü:`,
    detectedFolders
  );

  if (detectedFolders.length === 0) {
    console.log("‚ö†Ô∏è works„Éï„Ç©„É´„ÉÄ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü");
    return existingWorks;
  }

  // Êó¢Â≠ò„ÅÆID„ÇíÂèñÂæó
  const existingIds = new Set(existingWorks.map((work) => work.id));

  // Ê§úÂá∫„Åï„Çå„Åü„Éï„Ç©„É´„ÉÄ„Åã„Çâ„ÄÅÊó¢Â≠ò„Éá„Éº„Çø„Å´„Å™„ÅÑID„ÅÆworks„Éá„Éº„Çø„ÇíÁîüÊàê
  const newWorks: Works[] = [];
  for (const id of detectedFolders) {
    if (!existingIds.has(id)) {
      const defaultWork = generateDefaultWorkData(id);
      newWorks.push(defaultWork);
      console.log(`üìù Êñ∞Ë¶èworks„Éá„Éº„Çø„ÇíÁîüÊàê„Åó„Åæ„Åó„Åü: ID ${id}`);
    }
  }

  if (newWorks.length === 0) {
    console.log("‚ÑπÔ∏è Êñ∞Ë¶è„ÅÆworks„Éá„Éº„Çø„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü");
    return existingWorks;
  }

  // Êó¢Â≠ò„Éá„Éº„Çø„Å®„Éû„Éº„Ç∏
  const mergedWorks = mergeWorksData(existingWorks, newWorks);
  console.log(
    `‚úÖ works„Éá„Éº„Çø„Çí„Éû„Éº„Ç∏„Åó„Åæ„Åó„Åü: ÂêàË®à ${mergedWorks.length}‰ª∂`
  );

  return mergedWorks;
}
