// ブログカテゴリの定数定義
export const BLOG_CATEGORIES = [
  "技術",
  "開発",
  "デザイン",
  "学習",
  "Tips",
  "その他",
] as const;

export type BlogCategory = (typeof BLOG_CATEGORIES)[number];

// 技術スタックの定数定義（works.tsから再利用）
export type Technology = string;

export interface BlogPost {
  id: number;
  title: string;
  slug: string;
  date: string; // YYYY-MM-DD
  category: BlogCategory[];
  tags: string[];
  excerpt: string; // 記事の抜粋
  content: string; // 記事本文（Markdown形式を想定）
  thumbnail?: string;
  technologies?: Technology[];
  isPublished: boolean;
  createdAt?: string;
  updatedAt?: string;
}

// データバリデーション関数
export function validateBlogData(data: any): data is BlogPost {
  return (
    typeof data.id === "number" &&
    typeof data.title === "string" &&
    typeof data.slug === "string" &&
    typeof data.date === "string" &&
    Array.isArray(data.category) &&
    Array.isArray(data.tags) &&
    typeof data.excerpt === "string" &&
    typeof data.content === "string" &&
    typeof data.isPublished === "boolean"
  );
}

// サムネイルパスを自動生成する関数
export function generateBlogThumbnailPath(id: number): string {
  return `/images/blog/${id}/thumbnail.webp`;
}

// BlogPostオブジェクトにサムネイルパスを設定する関数
export function setBlogThumbnailPath(
  post: Omit<BlogPost, "thumbnail">
): BlogPost {
  return {
    ...post,
    thumbnail: generateBlogThumbnailPath(post.id),
  };
}

// 初期データ
export const blogData: BlogPost[] = [
  {
    id: 1,
    title: "セマンティックHTMLとARIA属性のメモ",
    slug: "html5-semantic-elements-accessibility",
    date: "2024-12-15",
    category: ["技術", "Tips"],
    tags: ["HTML5", "アクセシビリティ", "ARIA", "メモ"],
    excerpt: "セマンティック要素とARIA属性の組み合わせ方。フォームや動的コンテンツの実装パターン。",
    content: `
      <h2>はじめに</h2>
      <p>HTML5のセマンティック要素は、単にコードを読みやすくするだけでなく、アクセシビリティの向上とSEO最適化において重要な役割を果たします。本記事では、上級者向けの実装テクニックを解説します。</p>

      <h2>セマンティック要素の階層構造</h2>
      <p>適切な階層構造を理解することは、アクセシビリティの基盤となります。</p>

      <pre><code>&lt;article&gt;
  &lt;header&gt;
    &lt;h1&gt;記事タイトル&lt;/h1&gt;
    &lt;time datetime="2024-12-15"&gt;2024年12月15日&lt;/time&gt;
  &lt;/header&gt;
  &lt;section aria-labelledby="section1"&gt;
    &lt;h2 id="section1"&gt;セクション1&lt;/h2&gt;
    &lt;p&gt;コンテンツ...&lt;/p&gt;
  &lt;/section&gt;
  &lt;aside aria-label="関連情報"&gt;
    &lt;nav aria-label="関連記事"&gt;
      &lt;h3&gt;関連記事&lt;/h3&gt;
      &lt;ul&gt;
        &lt;li&gt;&lt;a href="#"&gt;記事1&lt;/a&gt;&lt;/li&gt;
      &lt;/ul&gt;
    &lt;/nav&gt;
  &lt;/aside&gt;
&lt;/article&gt;</code></pre>

      <h2>ARIA属性との適切な組み合わせ</h2>
      <p>セマンティック要素だけでは表現できない情報を、ARIA属性で補完します。</p>

      <pre><code>&lt;nav aria-label="メインナビゲーション"&gt;
  &lt;ul role="menubar"&gt;
    &lt;li role="none"&gt;
      &lt;a href="/" role="menuitem" aria-current="page"&gt;ホーム&lt;/a&gt;
    &lt;/li&gt;
    &lt;li role="none"&gt;
      &lt;a href="/about" role="menuitem"&gt;About&lt;/a&gt;
    &lt;/li&gt;
  &lt;/ul&gt;
&lt;/nav&gt;

&lt;section aria-labelledby="features-heading" aria-describedby="features-desc"&gt;
  &lt;h2 id="features-heading"&gt;機能一覧&lt;/h2&gt;
  &lt;p id="features-desc"&gt;以下の機能をご利用いただけます&lt;/p&gt;
  &lt;ul role="list"&gt;
    &lt;li role="listitem"&gt;機能1&lt;/li&gt;
  &lt;/ul&gt;
&lt;/section&gt;</code></pre>

      <h2>ランドマーク要素の活用</h2>
      <p>ページ構造を明確にするため、ランドマーク要素を適切に配置します。</p>

      <pre><code>&lt;body&gt;
  &lt;header role="banner"&gt;
    &lt;nav aria-label="サイトナビゲーション"&gt;...&lt;/nav&gt;
  &lt;/header&gt;

  &lt;main role="main"&gt;
    &lt;article&gt;...&lt;/article&gt;
  &lt;/main&gt;

  &lt;aside role="complementary" aria-label="サイドバー"&gt;
    &lt;section aria-labelledby="related-heading"&gt;
      &lt;h2 id="related-heading"&gt;関連情報&lt;/h2&gt;
    &lt;/section&gt;
  &lt;/aside&gt;

  &lt;footer role="contentinfo"&gt;
    &lt;p&gt;© 2024 My Portfolio&lt;/p&gt;
  &lt;/footer&gt;
&lt;/body&gt;</code></pre>

      <h2>フォームのアクセシビリティ</h2>
      <p>フォーム要素には、適切なラベルとエラー処理が不可欠です。</p>

      <pre><code>&lt;form aria-label="お問い合わせフォーム" novalidate&gt;
  &lt;fieldset&gt;
    &lt;legend&gt;お問い合わせ情報&lt;/legend&gt;

    &lt;div&gt;
      &lt;label for="name"&gt;お名前 &lt;span aria-label="必須"&gt;*&lt;/span&gt;&lt;/label&gt;
      &lt;input 
        type="text" 
        id="name" 
        name="name" 
        required 
        aria-required="true"
        aria-describedby="name-error name-hint"
        aria-invalid="false"
      &gt;
      &lt;span id="name-hint" class="hint"&gt;フルネームで入力してください&lt;/span&gt;
      &lt;span id="name-error" class="error" role="alert" aria-live="polite"&gt;&lt;/span&gt;
    &lt;/div&gt;

    &lt;div&gt;
      &lt;label for="email"&gt;メールアドレス &lt;span aria-label="必須"&gt;*&lt;/span&gt;&lt;/label&gt;
      &lt;input 
        type="email" 
        id="email" 
        name="email" 
        required 
        aria-required="true"
        aria-describedby="email-error"
        aria-invalid="false"
      &gt;
      &lt;span id="email-error" class="error" role="alert" aria-live="polite"&gt;&lt;/span&gt;
    &lt;/div&gt;
  &lt;/fieldset&gt;

  &lt;button type="submit"&gt;送信&lt;/button&gt;
&lt;/form&gt;</code></pre>

      <h2>動的コンテンツのライブリージョン</h2>
      <p>JavaScriptで動的に更新されるコンテンツには、適切なライブリージョンを設定します。</p>

      <pre><code>&lt;div 
  id="notification" 
  role="status" 
  aria-live="polite" 
  aria-atomic="true"
  class="sr-only"
&gt;
  通知メッセージがここに表示されます
&lt;/div&gt;

&lt;div 
  id="error-message" 
  role="alert" 
  aria-live="assertive" 
  aria-atomic="true"
&gt;
  エラーメッセージ
&lt;/div&gt;

&lt;div 
  id="progress" 
  role="progressbar" 
  aria-valuenow="0" 
  aria-valuemin="0" 
  aria-valuemax="100"
  aria-label="読み込み進捗"
&gt;
  &lt;div style="width: 0%"&gt;&lt;/div&gt;
&lt;/div&gt;</code></pre>

      <h2>まとめ</h2>
      <p>セマンティックHTMLとARIA属性を適切に組み合わせることで、すべてのユーザーがアクセスしやすいWebサイトを構築できます。WCAG 2.1のガイドラインに準拠した実装を心がけましょう。</p>
    `,
    isPublished: true,
    technologies: ["HTML5", "ARIA", "WCAG"],
    createdAt: "2024-12-15T00:00:00Z",
    updatedAt: "2024-12-15T00:00:00Z",
  },
  {
    id: 2,
    title: "パフォーマンス最適化のためのHTMLテクニックとリソースヒント",
    slug: "html-performance-optimization-resource-hints",
    date: "2024-12-10",
    category: ["技術", "Tips"],
    tags: ["HTML", "パフォーマンス", "最適化", "リソースヒント", "Core Web Vitals"],
    excerpt: "HTMLレベルでのパフォーマンス最適化手法を解説します。リソースヒント、プリロード、画像最適化など、実践的なテクニックを紹介します。",
    content: `
      <h2>はじめに</h2>
      <p>Webサイトのパフォーマンスは、ユーザー体験とSEOに直接影響します。本記事では、HTMLレベルで実装できるパフォーマンス最適化テクニックを解説します。</p>

      <h2>リソースヒントの活用</h2>
      <p>リソースヒントを使用することで、ブラウザにリソースの優先順位や接続方法を指示できます。</p>

      <h3>DNS Prefetch</h3>
      <pre><code>&lt;link rel="dns-prefetch" href="https://fonts.googleapis.com"&gt;
&lt;link rel="dns-prefetch" href="https://www.google-analytics.com"&gt;</code></pre>

      <h3>Preconnect</h3>
      <pre><code>&lt;link rel="preconnect" href="https://fonts.googleapis.com" crossorigin&gt;
&lt;link rel="preconnect" href="https://fonts.gstatic.com" crossorigin&gt;</code></pre>

      <h3>Prefetch</h3>
      <pre><code>&lt;!-- 次のページで使用される可能性が高いリソース --&gt;
&lt;link rel="prefetch" href="/next-page.html" as="document"&gt;
&lt;link rel="prefetch" href="/images/hero.jpg" as="image"&gt;</code></pre>

      <h3>Preload</h3>
      <pre><code>&lt;!-- 現在のページで重要なリソース --&gt;
&lt;link rel="preload" href="/fonts/main.woff2" as="font" type="font/woff2" crossorigin&gt;
&lt;link rel="preload" href="/css/critical.css" as="style"&gt;
&lt;link rel="preload" href="/js/main.js" as="script"&gt;</code></pre>

      <h3>Prerender</h3>
      <pre><code>&lt;link rel="prerender" href="/next-page.html"&gt;</code></pre>

      <h2>画像の最適化</h2>
      <p>モダンな画像フォーマットとレスポンシブ画像を活用します。</p>

      <h3>picture要素とsrcset</h3>
      <pre><code>&lt;picture&gt;
  &lt;source 
    media="(min-width: 1200px)" 
    srcset="hero-large.webp 1x, hero-large@2x.webp 2x"
    type="image/webp"
  &gt;
  &lt;source 
    media="(min-width: 768px)" 
    srcset="hero-medium.webp 1x, hero-medium@2x.webp 2x"
    type="image/webp"
  &gt;
  &lt;source 
    srcset="hero-small.webp 1x, hero-small@2x.webp 2x"
    type="image/webp"
  &gt;
  &lt;img 
    src="hero-fallback.jpg" 
    alt="ヒーロー画像"
    loading="lazy"
    decoding="async"
    width="1920"
    height="1080"
  &gt;
&lt;/picture&gt;</code></pre>

      <h3>loading属性とdecoding属性</h3>
      <pre><code>&lt;img 
  src="image.jpg" 
  alt="説明"
  loading="lazy"
  decoding="async"
  width="800"
  height="600"
&gt;

&lt;!-- 重要な画像は即座に読み込む --&gt;
&lt;img 
  src="hero.jpg" 
  alt="ヒーロー"
  loading="eager"
  fetchpriority="high"
  width="1920"
  height="1080"
&gt;</code></pre>

      <h2>フォントの最適化</h2>
      <p>Webフォントの読み込みを最適化します。</p>

      <pre><code>&lt;link rel="preconnect" href="https://fonts.googleapis.com"&gt;
&lt;link rel="preconnect" href="https://fonts.gstatic.com" crossorigin&gt;
&lt;link 
  rel="preload" 
  href="/fonts/main.woff2" 
  as="font" 
  type="font/woff2" 
  crossorigin
&gt;
&lt;link 
  href="https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400;700&display=swap" 
  rel="stylesheet"
&gt;

&lt;style&gt;
  @font-face {
    font-family: 'CustomFont';
    src: url('/fonts/custom.woff2') format('woff2'),
         url('/fonts/custom.woff') format('woff');
    font-display: swap;
    font-weight: 400;
    font-style: normal;
  }
&lt;/style&gt;</code></pre>

      <h2>JavaScriptの最適化</h2>
      <p>JavaScriptの読み込み方法を最適化します。</p>

      <pre><code>&lt;!-- 重要なスクリプトは即座に読み込む --&gt;
&lt;script src="/js/critical.js" defer&gt;&lt;/script&gt;

&lt;!-- 非同期読み込み --&gt;
&lt;script src="/js/analytics.js" async&gt;&lt;/script&gt;

&lt;!-- モジュール形式 --&gt;
&lt;script type="module" src="/js/main.js"&gt;&lt;/script&gt;

&lt;!-- インラインスクリプトの最適化 --&gt;
&lt;script&gt;
  // インラインスクリプトは最小限に
  window.__INITIAL_STATE__ = { /* ... */ };
&lt;/script&gt;</code></pre>

      <h2>メタタグの最適化</h2>
      <p>パフォーマンスに関連するメタタグを設定します。</p>

      <pre><code>&lt;meta name="viewport" content="width=device-width, initial-scale=1.0"&gt;
&lt;meta http-equiv="X-UA-Compatible" content="IE=edge"&gt;

&lt;!-- テーマカラー --&gt;
&lt;meta name="theme-color" content="#000000"&gt;
&lt;meta name="msapplication-TileColor" content="#000000"&gt;

&lt;!-- DNS Prefetch --&gt;
&lt;meta http-equiv="x-dns-prefetch-control" content="on"&gt;</code></pre>

      <h2>Critical CSSのインライン化</h2>
      <p>Above the foldのCSSをインライン化します。</p>

      <pre><code>&lt;style&gt;
  /* Critical CSS */
  body { margin: 0; font-family: sans-serif; }
  .header { background: #000; color: #fff; }
  /* ... */
&lt;/style&gt;
&lt;link rel="preload" href="/css/main.css" as="style" onload="this.onload=null;this.rel='stylesheet'"&gt;
&lt;noscript&gt;&lt;link rel="stylesheet" href="/css/main.css"&gt;&lt;/noscript&gt;</code></pre>

      <h2>まとめ</h2>
      <p>HTMLレベルでの最適化は、追加のJavaScriptやビルドツールなしでも実装できる効果的な手法です。リソースヒント、画像最適化、フォント最適化を組み合わせることで、Core Web Vitalsのスコアを向上させることができます。</p>
    `,
    isPublished: true,
    technologies: ["HTML5", "Performance", "Resource Hints"],
    createdAt: "2024-12-10T00:00:00Z",
    updatedAt: "2024-12-10T00:00:00Z",
  },
  {
    id: 3,
    title: "モダンなHTML APIとWeb Componentsの実践的活用",
    slug: "modern-html-apis-web-components",
    date: "2024-12-05",
    category: ["技術", "開発"],
    tags: ["HTML5", "Web Components", "Custom Elements", "Shadow DOM", "Web API"],
    excerpt: "HTML5のモダンなAPIとWeb Componentsを活用した、再利用可能で保守性の高いコンポーネント設計を解説します。Custom Elements、Shadow DOM、Template要素の実践的な使い方を紹介します。",
    content: `
      <h2>はじめに</h2>
      <p>モダンなHTML APIとWeb Componentsを活用することで、フレームワークに依存しない、再利用可能なコンポーネントを構築できます。本記事では、実践的な実装方法を解説します。</p>

      <h2>Template要素の活用</h2>
      <p>Template要素を使用することで、JavaScriptで動的に生成するHTMLを効率的に管理できます。</p>

      <pre><code>&lt;template id="card-template"&gt;
  &lt;div class="card"&gt;
    &lt;div class="card-header"&gt;
      &lt;h3 class="card-title"&gt;&lt;/h3&gt;
    &lt;/div&gt;
    &lt;div class="card-body"&gt;
      &lt;p class="card-content"&gt;&lt;/p&gt;
    &lt;/div&gt;
    &lt;div class="card-footer"&gt;
      &lt;button class="card-button"&gt;詳細を見る&lt;/button&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/template&gt;

&lt;script&gt;
  const template = document.getElementById('card-template');
  const cardData = [
    { title: 'タイトル1', content: 'コンテンツ1' },
    { title: 'タイトル2', content: 'コンテンツ2' }
  ];

  cardData.forEach(data => {
    const clone = template.content.cloneNode(true);
    clone.querySelector('.card-title').textContent = data.title;
    clone.querySelector('.card-content').textContent = data.content;
    document.body.appendChild(clone);
  });
&lt;/script&gt;</code></pre>

      <h2>Custom Elementsの実装</h2>
      <p>Custom Elementsを使用して、独自のHTML要素を定義します。</p>

      <h3>基本的なCustom Element</h3>
      <pre><code>&lt;script&gt;
  class CustomButton extends HTMLElement {
    constructor() {
      super();
      this.attachShadow({ mode: 'open' });
    }

    connectedCallback() {
      this.render();
      this.addEventListener('click', this.handleClick);
    }

    disconnectedCallback() {
      this.removeEventListener('click', this.handleClick);
    }

    static get observedAttributes() {
      return ['variant', 'disabled'];
    }

    attributeChangedCallback(name, oldValue, newValue) {
      if (oldValue !== newValue) {
        this.render();
      }
    }

    handleClick = (e) => {
      if (this.hasAttribute('disabled')) {
        e.preventDefault();
        return;
      }
      this.dispatchEvent(new CustomEvent('custom-click', {
        detail: { timestamp: Date.now() },
        bubbles: true
      }));
    }

    render() {
      const variant = this.getAttribute('variant') || 'primary';
      const disabled = this.hasAttribute('disabled');

      this.shadowRoot.innerHTML = \`
        &lt;style&gt;
          :host {
            display: inline-block;
          }
          button {
            padding: 12px 24px;
            border: none;
            border-radius: 4px;
            cursor: pointer;
            font-size: 16px;
            transition: all 0.3s;
          }
          button.primary {
            background: #007bff;
            color: white;
          }
          button.secondary {
            background: #6c757d;
            color: white;
          }
          button:disabled {
            opacity: 0.5;
            cursor: not-allowed;
          }
        &lt;/style&gt;
        &lt;button class="\${variant}" \${disabled ? 'disabled' : ''}&gt;
          &lt;slot&gt;&lt;/slot&gt;
        &lt;/button&gt;
      \`;
    }
  }

  customElements.define('custom-button', CustomButton);
&lt;/script&gt;

&lt;custom-button variant="primary"&gt;クリック&lt;/custom-button&gt;
&lt;custom-button variant="secondary" disabled&gt;無効&lt;/custom-button&gt;</code></pre>

      <h2>Shadow DOMの活用</h2>
      <p>Shadow DOMを使用して、スタイルとDOMのカプセル化を実現します。</p>

      <pre><code>&lt;script&gt;
  class ModalDialog extends HTMLElement {
    constructor() {
      super();
      this.attachShadow({ mode: 'open' });
    }

    connectedCallback() {
      this.render();
      this.setupEventListeners();
    }

    setupEventListeners() {
      this.shadowRoot.querySelector('.close-button')
        .addEventListener('click', () => this.close());

      this.shadowRoot.querySelector('.overlay')
        .addEventListener('click', (e) => {
          if (e.target === e.currentTarget) {
            this.close();
          }
        });
    }

    open() {
      this.setAttribute('open', '');
    }

    close() {
      this.removeAttribute('open');
      this.dispatchEvent(new CustomEvent('close'));
    }

    render() {
      this.shadowRoot.innerHTML = \`
        &lt;style&gt;
          :host {
            display: none;
          }
          :host([open]) {
            display: block;
          }
          .overlay {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: rgba(0, 0, 0, 0.5);
            display: flex;
            align-items: center;
            justify-content: center;
            z-index: 1000;
          }
          .dialog {
            background: white;
            border-radius: 8px;
            padding: 24px;
