import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { Button } from "../components/Button";
import BasicAuth from "../components/BasicAuth";
import ImageUpload from "../components/ImageUpload";
import {
  Works,
  WORK_CATEGORIES,
  TECHNOLOGIES,
  validateWorksData,
} from "../data/works";
import { useAuth } from "../hooks/useAuth";
import { useWorksData } from "../hooks/useWorksData";

export default function Admin() {
  const { isAuthenticated, isLoading, login, logout } = useAuth();
  const {
    works,
    isLoading: dataLoading,
    addWork,
    updateWork,
    deleteWork,
  } = useWorksData();
  const [editingWork, setEditingWork] = useState<Works | null>(null);
  const [showForm, setShowForm] = useState(false);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const formRef = useRef<HTMLDivElement>(null);
  const [formData, setFormData] = useState<Partial<Works>>({
    title: "",
    date: "",
    role: "",
    client: "",
    industry: "",
    technologies: [],
    category: [],
    details: {
      overview: [],
      challenge: [],
      solution: [],
      result: [],
      features: [],
      link: "",
    },
    mediaData: {
      images: [],
      videos: [],
    },
    isVisible: true,
  });

  // ãƒ•ã‚©ãƒ¼ãƒ ãŒè¡¨ç¤ºã•ã‚ŒãŸã¨ãã«è‡ªå‹•ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«
  useEffect(() => {
    if (showForm && formRef.current) {
      setTimeout(() => {
        formRef.current?.scrollIntoView({ behavior: "smooth", block: "start" });
      }, 100);
    }
  }, [showForm]);

  // èªè¨¼ãŒå¿…è¦ãªå ´åˆã®å‡¦ç†
  if (isLoading || dataLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen flex-col relative">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4"></div>
          <p className="text-gray-300">èª­ã¿è¾¼ã¿ä¸­...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="flex items-center justify-center min-h-screen flex-col relative">
        <div className="text-center">
          <h2>
            ADMIN
            <span>ç®¡ç†ç”»é¢</span>
          </h2>
          <p className="mb-10">
            ã“ã®ãƒšãƒ¼ã‚¸ã«ã‚¢ã‚¯ã‚»ã‚¹ã™ã‚‹ã«ã¯ãƒ­ã‚°ã‚¤ãƒ³ãŒå¿…è¦ã§ã™ã€‚
          </p>
          <Button onClick={() => setShowAuthModal(true)}>Login</Button>
        </div>
        {showAuthModal && (
          <BasicAuth
            onAuthSuccess={() => {
              setShowAuthModal(false);
              login();
            }}
            onAuthCancel={() => setShowAuthModal(false)}
          />
        )}
      </div>
    );
  }

  const handleInputChange = (field: string, value: any) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleDetailsChange = (field: string, value: any) => {
    setFormData((prev) => ({
      ...prev,
      details: {
        ...prev.details!,
        [field]: value,
      },
    }));
  };

  // å°†æ¥ã®ä½¿ç”¨ã®ãŸã‚ã«ä¿æŒï¼ˆç¾åœ¨ã¯æœªä½¿ç”¨ï¼‰
  // const handleArrayFieldChange = (field: string, value: string) => {
  //   const lines = value.split('\n').filter(line => line.trim() !== '');
  //   setFormData((prev) => ({
  //     ...prev,
  //     details: {
  //       ...prev.details!,
  //       [field]: lines,
  //     },
  //   }));
  // };

  const handleArrayFieldAdd = (field: string) => {
    setFormData((prev) => ({
      ...prev,
      details: {
        ...prev.details!,
        [field]: [
          ...((prev.details?.[
            field as keyof typeof prev.details
          ] as string[]) || []),
          "",
        ],
      },
    }));
  };

  const handleArrayFieldRemove = (field: string, index: number) => {
    setFormData((prev) => {
      const currentArray =
        (prev.details?.[field as keyof typeof prev.details] as string[]) || [];
      const newArray = currentArray.filter((_, i) => i !== index);
      return {
        ...prev,
        details: {
          ...prev.details!,
          [field]: newArray,
        },
      };
    });
  };

  const handleArrayFieldItemChange = (
    field: string,
    index: number,
    value: string
  ) => {
    setFormData((prev) => {
      const currentArray =
        (prev.details?.[field as keyof typeof prev.details] as string[]) || [];
      const newArray = [...currentArray];
      newArray[index] = value;
      return {
        ...prev,
        details: {
          ...prev.details!,
          [field]: newArray,
        },
      };
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log("ğŸ“ ãƒ•ã‚©ãƒ¼ãƒ é€ä¿¡é–‹å§‹...", { editingWork, formData });

    // å¿…é ˆãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã®ãƒã‚§ãƒƒã‚¯
    if (
      !formData.title ||
      !formData.date ||
      !formData.details?.overview?.length
    ) {
      alert("å¿…é ˆãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„");
      return;
    }

    const workId = editingWork?.id || Date.now();
    const newWork: Works = {
      id: workId,
      title: formData.title || "",
      date: formData.date || "",
      role: formData.role || "",
      client: formData.client || "",
      industry: formData.industry || "",
      technologies: formData.technologies || [],
      category: formData.category || [],
      thumbnail: `/images/works/${workId}/thumbnail.webp`, // è‡ªå‹•ç”Ÿæˆ
      details: {
        overview: formData.details?.overview || [],
        challenge: formData.details?.challenge || [],
        solution: formData.details?.solution || [],
        result: formData.details?.result || [],
        features: formData.details?.features || [],
        link: formData.details?.link || "",
      },
      mediaData: {
        images: formData.mediaData?.images || [],
        videos: formData.mediaData?.videos || [],
      },
      isVisible: formData.isVisible ?? true,
      createdAt: editingWork?.createdAt || new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    if (!validateWorksData(newWork)) {
      alert("ãƒ‡ãƒ¼ã‚¿ã®å½¢å¼ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“");
      return;
    }

    if (editingWork) {
      console.log("ğŸ”„ æ—¢å­˜ä½œå“ã‚’æ›´æ–°ä¸­...", editingWork.id);
      await updateWork(editingWork.id, newWork);
    } else {
      console.log("â• æ–°è¦ä½œå“ã‚’è¿½åŠ ä¸­...");
      await addWork(newWork);
    }

    console.log("âœ… ä¿å­˜å®Œäº†");
    setShowForm(false);
    setEditingWork(null);
    setFormData({
      title: "",
      date: "",
      role: "",
      client: "",
      industry: "",
      technologies: [],
      category: [],
      thumbnail: "",
      details: {
        overview: [],
        challenge: [],
        solution: [],
        result: [],
        features: [],
        link: "",
      },
      mediaData: {
        images: [],
        videos: [],
      },
      isVisible: true,
    });
  };

  const handleEdit = (work: Works) => {
    console.log("ğŸ“ ç·¨é›†ãƒœã‚¿ãƒ³ãŒã‚¯ãƒªãƒƒã‚¯ã•ã‚Œã¾ã—ãŸ:", work);
    setEditingWork(work);
    setFormData(work);
    setShowForm(true);
  };

  const handleDelete = async (id: number) => {
    if (window.confirm("ã“ã®ä½œå“ã‚’å‰Šé™¤ã—ã¾ã™ã‹ï¼Ÿ")) {
      await deleteWork(id);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingWork(null);
    setFormData({
      title: "",
      date: "",
      role: "",
      client: "",
      industry: "",
      technologies: [],
      category: [],
      thumbnail: "",
      details: {
        overview: [],
        challenge: [],
        solution: [],
        result: [],
        features: [],
        link: "",
      },
      mediaData: {
        images: [],
        videos: [],
      },
      isVisible: true,
    });
  };

  return (
    <div className="flex min-h-screen flex-col relative">
      <section className="container max-w-6xl py-10 sm:py-20">
        <h2>
          ADMIN
          <span>ç®¡ç†ç”»é¢</span>
        </h2>

        <div className="flex flex-wrap gap-2 mb-10 md:mb-20">
          <Button
            onClick={() => {
              setEditingWork(null);
              setFormData({
                title: "",
                date: "",
                role: "",
                client: "",
                industry: "",
                technologies: [],
                category: [],
                details: {
                  overview: [],
                  challenge: [],
                  solution: [],
                  result: [],
                  features: [],
                  link: "",
                },
                mediaData: {
                  images: [],
                  videos: [],
                },
                isVisible: true,
              });
              setShowForm(true);
            }}
          >
            æ–°è¦è¿½åŠ 
          </Button>
          <Button
            variant="outline"
            onClick={async () => {
              const { realtimeUpdateJsonFile } = await import(
                "../lib/fileUtils"
              );
              await realtimeUpdateJsonFile("/works-dynamic.json", works);
            }}
          >
            ãƒ•ã‚¡ã‚¤ãƒ«ã‚’ãƒªã‚¢ãƒ«ã‚¿ã‚¤ãƒ æ›´æ–°
          </Button>
          <Button
            variant="outline"
            onClick={() => {
              window.location.reload();
            }}
          >
            ãƒ‡ãƒ¼ã‚¿ã‚’å†èª­ã¿è¾¼ã¿
          </Button>
          <Button
            variant="outline"
            onClick={() => {
              localStorage.removeItem("works-data-backup");
              console.log(
                "ğŸ—‘ï¸ ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã®ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚’ã‚¯ãƒªã‚¢ã—ã¾ã—ãŸ"
              );
              window.location.reload();
            }}
          >
            ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã‚’ã‚¯ãƒªã‚¢
          </Button>
          <Button
            variant="outline"
            onClick={async () => {
              try {
                // TypeScriptãƒ•ã‚¡ã‚¤ãƒ«ã‹ã‚‰ãƒ‡ãƒ¼ã‚¿ã‚’å¼·åˆ¶èª­ã¿è¾¼ã¿
                const { worksDynamicData } = await import(
                  "../data/works-dynamic"
                );
                console.log(
                  "ğŸ“ TypeScriptãƒ•ã‚¡ã‚¤ãƒ«ã‹ã‚‰èª­ã¿è¾¼ã‚“ã ãƒ‡ãƒ¼ã‚¿:",
                  worksDynamicData.length,
                  "ä»¶"
                );
                // ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã‚’ã‚¯ãƒªã‚¢ã—ã¦ãƒ•ã‚¡ã‚¤ãƒ«ãƒ‡ãƒ¼ã‚¿ã‚’è¨­å®š
                localStorage.removeItem("works-data-backup");
                localStorage.setItem(
                  "works-data-backup",
                  JSON.stringify(worksDynamicData)
                );
                console.log(
                  "âœ… TypeScriptãƒ•ã‚¡ã‚¤ãƒ«ãƒ‡ãƒ¼ã‚¿ã§ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã‚’æ›´æ–°ã—ã¾ã—ãŸ"
                );
                window.location.reload();
              } catch (error) {
                console.error("âŒ ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ:", error);
              }
            }}
          >
            TypeScriptãƒ•ã‚¡ã‚¤ãƒ«ã‹ã‚‰åŒæœŸ
          </Button>
          <Button
