import { Link, useSearchParams } from "react-router-dom";
import { useState, useEffect } from "react";

import { Button } from "../components/Button";
import Cta from "../components/Cta";
import { BlogList } from "../components/blog/BlogList";
import { CategoryList } from "../components/blog/CategoryList";
import BasicAuth from "../components/BasicAuth";

import { blogData } from "../data/blog";

// Metadata型はNext.js用のため、React Routerプロジェクトでは削除

export default function BlogPage() {
  const [searchParams] = useSearchParams();
  const selectedCategory = searchParams.get("category");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuth, setShowAuth] = useState(false);

  // 認証状態をチェック
  useEffect(() => {
    const checkAuth = () => {
      const authStatus = localStorage.getItem("blog_auth");
      const authTimestamp = localStorage.getItem("blog_auth_timestamp");

      if (authStatus === "true" && authTimestamp) {
        // 24時間以内の認証かチェック
        const now = Date.now();
        const authTime = parseInt(authTimestamp);
        const hoursDiff = (now - authTime) / (1000 * 60 * 60);

        if (hoursDiff < 24) {
          setIsAuthenticated(true);
        } else {
          // 認証期限切れ
          localStorage.removeItem("blog_auth");
          localStorage.removeItem("blog_auth_timestamp");
          setShowAuth(true);
        }
      } else {
        setShowAuth(true);
      }
    };

    checkAuth();
  }, []);

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuth(false);
    // ブログ用の認証状態を保存
    localStorage.setItem("blog_auth", "true");
    localStorage.setItem("blog_auth_timestamp", Date.now().toString());
  };

  const handleAuthCancel = () => {
    // 認証をキャンセルした場合はホームページにリダイレクト
    window.location.href = "/";
  };

  // カテゴリでフィルタリング
  const filteredPosts = blogData.filter((post) => {
    if (!selectedCategory) return post.isVisible;
    return post.isVisible && post.category.includes(selectedCategory);
  });

  // 認証されていない場合は認証画面を表示
  if (showAuth) {
    return (
      <BasicAuth
        onAuthSuccess={handleAuthSuccess}
        onAuthCancel={handleAuthCancel}
      />
    );
  }

  // 認証されていない場合はローディング表示
  if (!isAuthenticated) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4"></div>
          <p className="text-gray-400">認証を確認中...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen flex-col relative">
      <main className="flex-1">
        <section className="container max-w-6xl py-10 sm:py-20">
          <h2>
            BLOG
            <span>ブログ</span>
          </h2>

          {selectedCategory && (
            <div className="mb-8">
              <div className="bg-white/10 backdrop-blur-sm p-4 rounded-lg inline-block">
                <p className="text-primary-400 font-semibold">
                  カテゴリ: {selectedCategory}
                  <span className="ml-2 text-gray-400">
                    ({filteredPosts.length}件)
                  </span>
                </p>
              </div>
            </div>
          )}

          <div className="flex flex-col lg:flex-row gap-8">
            <div className="lg:w-3/4">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-5">
                {filteredPosts.length > 0 ? (
                  filteredPosts.map((post) => (
                    <BlogList key={post.id} post={post} />
                  ))
                ) : (
                  <div className="col-span-full text-center py-12">
                    <p className="text-gray-400 text-lg">
                      {selectedCategory
                        ? `「${selectedCategory}」カテゴリの記事が見つかりませんでした。`
                        : "記事が見つかりませんでした。"}
                    </p>
                    <Link to="/blog" className="inline-block mt-4">
                      <Button>すべての記事を見る</Button>
                    </Link>
                  </div>
                )}
              </div>
            </div>

            <div className="lg:w-1/4">
              <CategoryList articles={blogData} />
            </div>
          </div>

          <div className="mt-20 md:mt-28 text-center">
            <Link to="/">
              <Button>TOPへ戻る</Button>
            </Link>
          </div>
        </section>

        <Cta />
      </main>
    </div>
  );
}
