import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button } from "../components/Button";
import { ContactForm } from "../components/ContactForm";
import { LazyImage } from "../components/LazyImage";
import BasicAuth from "../components/BasicAuth";
import { useAuth } from "../hooks/useAuth";
import { useWorksData } from "../hooks/useWorksData";
// import { ColorPalette } from "../components/ColorPalette";

export default function Works() {
  const { isAuthenticated, isLoading, login, logout } = useAuth();
  const { works, isLoading: dataLoading } = useWorksData();
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("„Åô„Åπ„Å¶");

  // „Éá„Éê„ÉÉ„Ç∞Áî®„ÅÆ„É≠„Ç∞
  console.log("üîç Works „Ç≥„É≥„Éù„Éº„Éç„É≥„Éà - „Éá„Éº„ÇøÁä∂ÊÖã:", {
    works: works.length,
    dataLoading,
    isAuthenticated,
    isLoading,
  });

  // „Éá„Éº„Çø„ÅÆË©≥Á¥∞„Çí„É≠„Ç∞Âá∫Âäõ
  if (works.length > 0) {
    console.log(
      "üìä Ë™≠„ÅøËæº„Åæ„Çå„Åü‰ΩúÂìÅ„Éá„Éº„Çø:",
      works.map((work) => ({
        id: work.id,
        title: work.title,
        category: work.category,
      }))
    );
  }

  const allCategories = Array.from(
    new Set(works.flatMap((work) => work.category))
  ).sort();

  // Ë™çË®º„ÅåÂøÖË¶Å„Å™Â†¥Âêà„ÅÆÂá¶ÁêÜ
  if (isLoading || dataLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen flex-col relative">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4"></div>
          <p className="text-gray-300">Ë™≠„ÅøËæº„Åø‰∏≠...</p>
        </div>
      </div>
    );
  }

  // Ë™çË®º„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅØË™çË®º„É¢„Éº„ÉÄ„É´„ÇíË°®Á§∫
  if (!isAuthenticated) {
    return (
      <div className="flex items-center justify-center min-h-screen flex-col relative">
        <div className="text-center">
          <h2>
            WORKS
            <span>Âà∂‰ΩúÂÆüÁ∏æ</span>
          </h2>
          <p className="mb-10 md:mb-20">
            „Åì„ÅÆ„Éö„Éº„Ç∏„Å´„Ç¢„ÇØ„Çª„Çπ„Åô„Çã„Å´„ÅØ„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ
          </p>
          <Button onClick={() => setShowAuthModal(true)}>Login</Button>
        </div>
        {showAuthModal && (
          <BasicAuth
            onAuthSuccess={() => {
              setShowAuthModal(false);
              login();
            }}
            onAuthCancel={() => setShowAuthModal(false)}
          />
        )}
      </div>
    );
  }

  return (
    <div className="flex min-h-screen flex-col relative">
      {/* <ColorPalette/> */}
      <section className="container max-w-6xl py-10 sm:py-20">
        <div className="flex justify-between items-center mb-8">
          <h2>
            WORKS
            <span>Âà∂‰ΩúÂÆüÁ∏æ</span>
          </h2>
          <Button variant="outline" onClick={logout} className="text-sm">
            Logout
          </Button>
        </div>

        <div>
          <div className="flex flex-wrap gap-2 md:gap-3 mb-16 md:mb-20">
            {["„Åô„Åπ„Å¶", ...allCategories].map((category) => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`text-xs md:text-sm px-3 md:px-5 py-1 md:py-1.5 rounded-full transition-colors ${
                  selectedCategory === category
                    ? "bg-primary-500 hover:bg-primary-600"
                    : "bg-gray-700 text-gray-300 hover:bg-gray-600"
                }`}
              >
                {category}
              </button>
            ))}
          </div>

          {works.length === 0 ? (
            <div className="text-center py-20">
              <p className="text-gray-400 mb-4">„Éá„Éº„Çø„ÅåË™≠„ÅøËæº„Åæ„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì</p>
              <p className="text-sm text-gray-500">
                „Ç≥„É≥„ÇΩ„Éº„É´„Åß„Ç®„É©„Éº„É≠„Ç∞„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10 md:gap-10 mb-28 md:mb-40">
              {works
                .filter(
                  (work) =>
                    selectedCategory === "„Åô„Åπ„Å¶" ||
                    work.category.includes(selectedCategory as any)
                )
                .map((work) => (
                  <Link
                    key={work.id}
                    to={`/works/${work.id}`}
                    className="backdrop-blur-sm overflow-hidden transition-all duration-300 group block"
                  >
                    <div className="mb-4 aspect-[8/5] overflow-hidden">
                      <LazyImage
                        src={`/images/works/${work.id}/thumbnail.webp`}
                        alt={work.title}
                        className="w-full h-full"
                      />
                    </div>

                    <div>
                      <div className="mb-1">
                        <div className="flex flex-wrap gap-2">
                          {work.category.map((cat, index) => (
                            <span
                              key={index}
                              className="text-xs md:text-sm text-primary-500 border-b border-transparent hover:border-primary-500 cursor-pointer"
                              onClick={(e) => {
                                e.preventDefault();
                                e.stopPropagation();
                                setSelectedCategory(cat);
                              }}
                            >
                              #{cat}
                            </span>
                          ))}
                        </div>
                      </div>

                      <h3 className="text-lg md:text-xl group-hover:text-primary-500 transition-colors">
                        {work.title}
                      </h3>
                    </div>
                  </Link>
                ))}
            </div>
          )}

          <div className="text-center mb-16 md:mb-20">
            <Link to="/">
              <Button variant="outline">Back to TOP</Button>
            </Link>
          </div>
        </div>
      </section>

      <ContactForm />
    </div>
  );
}
