import { useState, useEffect } from "react";
import { recentBooks, RecentBook } from "../data/recentBooks";

// コンポーネントで使用する書籍情報の型
export interface BookInfo {
  isbn: string;
  title: string;
  author: string;
  publisher: string;
  pubdate: string;
  cover: string;
  summary: string;
}

export function useRecentBooks() {
  const [books, setBooks] = useState<BookInfo[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    try {
      setIsLoading(true);
      setError(null);

      // データファイルから直接読み込み（openBD APIは終了しているため）
      const bookList: BookInfo[] = recentBooks.map((book) => ({
        isbn: book.isbn || "",
        title: book.title || "",
        author: book.author || "",
        publisher: book.publisher || "",
        pubdate: book.pubdate || "",
        cover: book.cover || "",
        summary: book.summary || "",
      }));

      console.log("✅ 書籍データ読み込み完了:", bookList.length, "件");
      setBooks(bookList);
    } catch (err) {
      console.error("❌ 書籍データの読み込みに失敗しました:", err);
      setError(
        err instanceof Error ? err.message : "不明なエラーが発生しました"
      );
      setBooks([]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  return { books, isLoading, error };
}
