import { useEffect } from 'react';

export const usePerformanceMonitor = () => {
  useEffect(() => {
    // Web Vitals„ÅÆÁõ£Ë¶ñ
    const reportWebVitals = (metric: any) => {
      console.log('üìä Performance Metric:', {
        name: metric.name,
        value: metric.value,
        delta: metric.delta,
        id: metric.id,
        navigationType: metric.navigationType,
      });

      // „Éë„Éï„Ç©„Éº„Éû„É≥„Çπ„ÅåÊÇ™„ÅÑÂ†¥Âêà„ÅÆË≠¶Âëä
      if (metric.name === 'LCP' && metric.value > 2500) {
        console.warn('‚ö†Ô∏è LCP„ÅåÈÅÖ„ÅÑ„Åß„Åô:', metric.value + 'ms (Êé®Â•®: <2500ms)');
      }
      if (metric.name === 'FID' && metric.value > 100) {
        console.warn('‚ö†Ô∏è FID„ÅåÈÅÖ„ÅÑ„Åß„Åô:', metric.value + 'ms (Êé®Â•®: <100ms)');
      }
      if (metric.name === 'CLS' && metric.value > 0.1) {
        console.warn('‚ö†Ô∏è CLS„ÅåÈ´ò„ÅÑ„Åß„Åô:', metric.value + ' (Êé®Â•®: <0.1)');
      }
    };

    // ÁîªÂÉèË™≠„ÅøËæº„ÅøÊôÇÈñì„ÅÆÁõ£Ë¶ñ
    const monitorImageLoading = () => {
      const images = document.querySelectorAll('img');
      images.forEach((img) => {
        const startTime = performance.now();

        img.addEventListener('load', () => {
          const loadTime = performance.now() - startTime;
          if (loadTime > 1000) {
            console.warn(`‚ö†Ô∏è ÁîªÂÉèË™≠„ÅøËæº„Åø„ÅåÈÅÖ„ÅÑ„Åß„Åô: ${img.src} (${loadTime.toFixed(0)}ms)`);
          }
        });
      });
    };

    // „Éê„É≥„Éâ„É´„Çµ„Ç§„Ç∫„ÅÆÁõ£Ë¶ñ
    const monitorBundleSize = () => {
      const scripts = document.querySelectorAll('script[src]');

      scripts.forEach((script) => {
        const src = script.getAttribute('src');
        if (src && src.includes('static/js/')) {
          // ÂÆüÈöõ„ÅÆ„Çµ„Ç§„Ç∫„ÅØÂèñÂæó„Åß„Åç„Å™„ÅÑ„Åü„ÇÅ„ÄÅ„Éï„Ç°„Ç§„É´Âêç„Åã„ÇâÊé®ÂÆö
          console.log('üì¶ Ë™≠„ÅøËæº„ÅøÊ∏à„Åø„Çπ„ÇØ„É™„Éó„Éà:', src);
        }
      });
    };

    // ÂàùÊúüÂåñ
    monitorImageLoading();
    monitorBundleSize();

    // Web Vitals„ÅÆÁõ£Ë¶ñ„ÇíË®≠ÂÆö
    if (typeof window !== 'undefined' && 'performance' in window) {
      // Êó¢Â≠ò„ÅÆreportWebVitalsÈñ¢Êï∞„Åå„ÅÇ„Çå„Å∞‰ΩøÁî®
      if (window.reportWebVitals) {
        window.reportWebVitals(reportWebVitals);
      }
    }

    return () => {
      // „ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó
    };
  }, []);
};

// „Ç∞„É≠„Éº„Éê„É´ÂûãÂÆöÁæ©
declare global {
  interface Window {
    reportWebVitals?: (metric: any) => void;
  }
}
