export interface Skill {
  name: string;
  image: string;
  alt?: string;
  isSquare?: boolean;
  years?: number; // 経験年数
}

export const frontendSkills: Skill[] = [
  { name: "HTML5", image: "html5.svg", years: 5 },
  { name: "CSS", image: "css.svg", years: 5 },
  { name: "Tailwind CSS", image: "tailwindcss-dark.svg", isSquare: false, years: 3 },
  { name: "Sass", image: "sass.png", years: 4 },
  { name: "Bootstrap", image: "bootstrap.svg", years: 4 },
  { name: "JavaScript", image: "javascript-badge.svg", years: 5 },
  { name: "React", image: "react.svg", years: 4 },
  { name: "TypeScript", image: "typescript-blue.svg", isSquare: false, years: 3 },
  { name: "Next.js", image: "nextjs-dark.svg", isSquare: false, years: 2 },
  { name: "Astro", image: "astro-dark.svg", isSquare: false, years: 1 },
];

export const backendSkills: Skill[] = [
  { name: "Node.js", image: "nodejs-dark.svg", years: 3 },
  { name: "PHP", image: "php.svg", years: 4 },
  { name: "MySQL", image: "mysql.png", isSquare: false, years: 4 },
];

export const designSkills: Skill[] = [
  { name: "Figma", image: "figma.svg", isSquare: false },
];

export const cmsSkills: Skill[] = [
  { name: "WordPress", image: "WordPress-dark.png", isSquare: false },
];

export const otherSkills: Skill[] = [
  { name: "GitHub", image: "github-dark.png" },
  { name: "microCMS", image: "microCMS_dark.svg", isSquare: false },
  { name: "Netlify", image: "netlify-dark.svg", isSquare: false },
  { name: "Vercel", image: "vercel-dark.svg", isSquare: false },
];

export const ecSkills: Skill[] = [
