import { useRecentBooks, BookInfo } from "../hooks/useRecentBooks";

function BookCard({ book }: { book: BookInfo }) {
  return (
    <div className="flex flex-col gap-4 md:gap-6">
    <div className="flex flex-col md:flex-row gap-4 md:gap-6">
      {book.cover && (
        <div className="flex-shrink-0">
          <img
            src={book.cover}
            alt={book.title}
            className="w-32 md:w-40 h-auto object-cover"
            loading="lazy"
          />
        </div>
      )}
      {/* <div className="flex-1 flex flex-col">
        <h4 className="text-lg md:text-xl font-bold mb-1 text-white">
          {book.title}
        </h4>
        {book.subtitle && (
          <p className="text-sm md:text-base text-gray-400 mb-2">
            {book.subtitle}
          </p>
        )}
        <div className="text-sm md:text-base text-gray-300 space-y-1 mb-3">
          {book.author && (
            <p>
              <span className="text-gray-400">著者:</span> {book.author}
            </p>
          )}
          {book.publisher && (
            <p>
              <span className="text-gray-400">出版社:</span> {book.publisher}
            </p>
          )}
          {book.pubdate && (
            <p>
              <span className="text-gray-400">発行日:</span> {book.pubdate}
            </p>
          )}
          {book.pageCount && (
            <p>
              <span className="text-gray-400">ページ数:</span> {book.pageCount}ページ
            </p>
          )}
          {book.categories && book.categories.length > 0 && (
            <p>
              <span className="text-gray-400">カテゴリ:</span>{" "}
              {book.categories.join(", ")}
            </p>
          )}
          {book.averageRating && (
            <p>
              <span className="text-gray-400">評価:</span>{" "}
              <span className="text-yellow-400">
                {"★".repeat(Math.round(book.averageRating))}
                {"☆".repeat(5 - Math.round(book.averageRating))}
              </span>{" "}
              {book.averageRating.toFixed(1)}
              {book.ratingsCount && (
                <span className="text-gray-500 text-xs ml-1">
                  ({book.ratingsCount}件の評価)
                </span>
              )}
            </p>
          )}
        </div>
        {book.summary && (
          <p className="text-sm text-gray-400 line-clamp-3 mb-3">
            {book.summary}
          </p>
        )}
        {(book.previewLink || book.infoLink) && (
          <div className="flex gap-3 mt-auto">
            {book.previewLink && (
              <a
                href={book.previewLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-sm text-primary-400 hover:text-primary-300 underline"
              >
                プレビュー
              </a>
            )}
            {book.infoLink && (
              <a
                href={book.infoLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-sm text-primary-400 hover:text-primary-300 underline"
              >
                詳細情報
              </a>
            )}
          </div>
        )}
      </div> */}
    </div>
    </div>
  );
}

export function RecentBooks() {
  const { books, isLoading, error } = useRecentBooks();

  if (isLoading) {
    return (
      <div className="text-center py-8">
        <p className="text-gray-400">書籍情報を読み込み中...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-400">エラー: {error}</p>
      </div>
    );
  }

  if (books.length === 0) {
    return (
      <div className="text-center py-8">
        <p className="text-gray-400">
          書籍情報が見つかりませんでした。
          <br />
          <span className="text-sm">
            ISBNコードが正しいか、openBDにデータが存在するか確認してください。
          </span>
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-6 md:space-y-8">
      {books.map((book) => (
        <BookCard key={book.isbn} book={book} />
      ))}
    </div>
  );
}
