import React, { useState } from "react";
import { useParams, Navigate } from "react-router-dom";
import { BlogArticleLayout } from "../components/blog/BlogArticleLayout";
import { DynamicSections } from "../components/blog/DynamicSections";
import BasicAuth from "../components/BasicAuth";
import { useBlogAuth } from "../hooks/useAuth";
import { blogData } from "../data/blog";

export default function BlogDetail() {
  const { slug } = useParams<{ slug: string }>();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuth, setShowAuth] = useState(false);

  // 認証状態をチェック
  useEffect(() => {
    const checkAuth = () => {
      const authStatus = localStorage.getItem("blog_auth");
      const authTimestamp = localStorage.getItem("blog_auth_timestamp");

      if (authStatus === "true" && authTimestamp) {
        // 24時間以内の認証かチェック
        const now = Date.now();
        const authTime = parseInt(authTimestamp);
        const hoursDiff = (now - authTime) / (1000 * 60 * 60);

        if (hoursDiff < 24) {
          setIsAuthenticated(true);
        } else {
          // 認証期限切れ
          localStorage.removeItem("blog_auth");
          localStorage.removeItem("blog_auth_timestamp");
          setShowAuth(true);
        }
      } else {
        setShowAuth(true);
      }
    };

    checkAuth();
  }, []);

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuth(false);
    // ブログ用の認証状態を保存
    localStorage.setItem("blog_auth", "true");
    localStorage.setItem("blog_auth_timestamp", Date.now().toString());
  };

  const handleAuthCancel = () => {
    // 認証をキャンセルした場合はブログ一覧ページにリダイレクト
    window.location.href = "/blog";
  };

  // slugに基づいて記事を検索
  const article = blogData.find((post) => post.slug === slug);

  // 記事が見つからない場合は404ページにリダイレクト
  if (!article) {
    return <Navigate to="/blog" replace />;
  }

  // セクションタイトルを抽出（目次用）
  const sectionTitles =
    article.sections?.map((section) => section.title).filter(Boolean) || [];

  // 認証されていない場合は認証画面を表示
  if (showAuth) {
    return (
      <BasicAuth
        onAuthSuccess={handleAuthSuccess}
        onAuthCancel={handleAuthCancel}
      />
    );
  }

  // 認証されていない場合はローディング表示
  if (!isAuthenticated) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4"></div>
          <p className="text-gray-400">認証を確認中...</p>
        </div>
      </div>
    );
  }

  return (
    <BlogArticleLayout
      article={article}
      sectionTitles={sectionTitles}
      showTableOfContents={true}
      showRandomArticles={true}
      showCategoryList={true}
      showCta={true}
      showBackToBlog={true}
    >
      {/* 記事の動的セクションをレンダリング */}
      {article.sections && (
        <DynamicSections sections={article.sections} articleId={article.id.toString()} />
      )}
    </BlogArticleLayout>
  );
}
