              </div>

              <div>
                <label className="block text-sm font-medium mb-2">æˆæžœ</label>
                <div className="space-y-2">
                  {(formData.details?.result || []).map((item, index) => (
                    <div key={index} className="flex gap-2">
                      <textarea
                        value={item}
